import Head from 'next/head'
import Nav from '../components/Nav';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCompactDisc, faMusic, faMicrophone } from '@fortawesome/free-solid-svg-icons'

const Index = () => (
  <div>
    <Head>
      <title>ALbum</title>
    </Head>

    <Nav />

    <h1>Welcome to ALbum</h1>

    <div className='music-box'>
      <a href='/playlist' className='card'>
        <h2><FontAwesomeIcon icon={faCompactDisc} /></h2>
        <h2>Create a Playlist</h2>
      </a>


      <a href='/genres' className='card'>
      <h2><FontAwesomeIcon icon={faMusic} /></h2>
        <h2>Search Genres</h2>
      </a>

      <a href='/artists' className='card'>
      <h2><FontAwesomeIcon icon={faMicrophone} /></h2>
        <h2>Discover Artists</h2>
      </a>

    </div>

    <style jsx global>{`
        body {
          background-color: #203b4f;
          font-family: sans-serif;
        }
        h1 {
          color: #f5f5f5;
          text-align: center;
        }
        .music-box {
          max-width: 880px;
          margin 60px auto 40px;
          display: flex;
          flex-direction: row;
          justify-content: space-around;
        }
        .music-box h2 {
          color: #333;
        }
        .card {
          text-align: center;
          text-decoration: none;
          width: 220px;
          padding: 18px 18px 24px;
          background-color: #b89911;
          border-radius: 6px;
        }
    `}</style>
  </div>
);



export default Index;
